query:
-- 1. 更新产品分类表，添加新字段
ALTER TABLE productCategories 
ADD COLUMN slug VARCHAR(255) NOT NULL DEFAULT '',
ADD COLUMN parentId INT NULL;

-- 2. 修改imageUrl为可空
ALTER TABLE productCategories MODIFY COLUMN imageUrl TEXT NULL;

-- 3. 为现有记录生成slug
UPDATE productCategories SET slug = CONCAT('category-', id) WHERE slug = '';

